data-source.init-script:
CREATE TABLE USERS (
                       ID INT NOT NULL AUTO_INCREMENT PRIMARY KEY,
                       NAME VARCHAR2,
                       SURNAME VARCHAR2
);
CREATE TABLE ACCOUNTS (
                          ID INT NOT NULL AUTO_INCREMENT PRIMARY KEY,
                          NUMBER VARCHAR2(20),
                          FUNDS DOUBLE,
                          USER_ID INT,
                          FOREIGN KEY (USER_ID) REFERENCES USERS(ID)
);
CREATE TABLE CARDS (
                       ID INT NOT NULL AUTO_INCREMENT PRIMARY KEY ,
                       NUMBER VARCHAR2(20),
                       USER_ID INT,
                       FOREIGN KEY (USER_ID) REFERENCES USERS(ID)
);
